class TestCustomFileExtension(unittest.TestCase):
    def setUp(self):
        self.my_list = [1, 2]
        self.file_dir = './test_data_files/'
        self.file_ext = '.txt'
        os.makedirs(self.file_dir, exist_ok=True)
        for i in range(3):
            df = pd.DataFrame({'A': range(3), 'B': range(3, 6)})
            df.to_csv(f'{self.file_dir}file_{i}{self.file_ext}', index=False)

    def tearDown(self):
        for f in os.listdir(self.file_dir):
            os.remove(os.path.join(self.file_dir, f))
        os.rmdir(self.file_dir)

    def test_custom_file_extension(self):
        df = task_func(self.my_list, file_dir=self.file_dir, file_ext=self.file_ext)
        self.assertEqual(len(df), 9)

