class TestMissingKey(unittest.TestCase):
    def setUp(self):
        self.json_data = json.dumps({"another_key": "https://example.com/file.txt"})
        self.unknown_key = "unknown"

    def test_missing_key_raises_key_error(self):
        with self.assertRaises(KeyError):
            task_func(self.json_data, self.unknown_key)
